import React, { useState } from "react";
import axios from "axios";
import Cookies from "js-cookie";
axios.defaults.withCredentials = true;
function CreatePost() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);

  const SubmitPost = () => {
    const data = {
      title: title,
      description: description,
    };
    axios
      .post("http://localhost:3001/try", { withCredentials: true }, data)
      .then((response) => {
        console.log(response);
      });
  };

  return (
    <div>
      <div id="CreatePostContainer">
        <div id="CreatePostContainerTitle">
          <label htmlFor="titleInput">Title:</label>
          <input
            id="titleInput"
            type="text"
            placeholder="Enter title"
            onChange={(e) => {
              setTitle(e.target.value);
            }}
          />
        </div>
        <div id="CreatePostContainerDescription">
          <label htmlFor="descriptionInput">Description:</label>
          <textarea
            id="descriptionInput"
            placeholder="Enter description"
            rows="4"
            cols="50"
            onChange={(e) => {
              setDescription(e.target.value);
            }}
          ></textarea>
        </div>
        <div id="CreatePostContainerFile">
          <label htmlFor="fileInput">Upload File:</label>
          <input
            id="fileInput"
            type="file"
            onChange={(e) => {
              setImage(e.target.files[0]);
            }}
          />
        </div>
        <button id="CreatePostSubmitButton" onClick={SubmitPost}>
          Submit
        </button>
      </div>
    </div>
  );
}

export default CreatePost;
