import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

const SubmitPost = (values) => {
  // Access the values of 'title', 'description', and 'file' here
  console.log("Title:", values.title);
  console.log("Description:", values.description);
  console.log("File:", values.file);
};

function CreatePost() {
  const initialValues = {
    title: "",
    description: "",
    file: null,
  };

  const validationSchema = Yup.object({
    title: Yup.string().required("Title is required"),
    description: Yup.string().required("Description is required"),
    file: Yup.mixed().required("File is required"),
  });

  const handleSubmit = (values) => {
    SubmitPost(values);
  };

  return (
    <div>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        <Form>
          <div id="CreatePostContainer">
            <div id="CreatePostContainerTitle">
              <label htmlFor="title">Title:</label>
              <Field
                type="text"
                id="title"
                name="title"
                placeholder="Enter title"
              />
              <ErrorMessage name="title" component="div" className="error" />
            </div>
            <div id="CreatePostContainerDescription">
              <label htmlFor="description">Description:</label>
              <Field
                as="textarea"
                id="description"
                name="description"
                placeholder="Enter description"
                rows="4"
                cols="50"
              />
              <ErrorMessage
                name="description"
                component="div"
                className="error"
              />
            </div>
            <div id="CreatePostContainerFile">
              <label htmlFor="file">Upload File:</label>
              <input
                id="file"
                type="file"
                name="file"
                onChange={(event) => {
                  const file = event.currentTarget.files[0];
                  setFieldValue("file", file);
                }}
              />
              <ErrorMessage name="file" component="div" className="error" />
            </div>
            <button type="submit" id="CreatePostSubmitButton">
              Submit
            </button>
          </div>
        </Form>
      </Formik>
    </div>
  );
}

export default CreatePost;
