import React, { useRef } from "react";
import NavBar from "./NavBar";
import submitIcon from "../Resources/Images/message.png";

function Chat() {
  return (
    <div>
      <NavBar />
      <div id="ChatPageDiv">
        <div id="ChatDiv">
          <div id="DmName">
            <img src={null} id="FriendLogo"></img>
            <label id="FriendName">Friend Name</label>
          </div>
          <div id="messageContainer"></div>
          <div id="WriteCommentContainer">
            <textarea
              id="addMessageInput"
              type="text"
              placeholder="Enter Comment"
            />
            <img id="SendMessage" src={submitIcon} alt="Submit" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
