import NavBar from "./NavBar";
import React, { useState, useEffect } from "react";
import defaultLogo from "../Resources/Images/defaultLogo.png";

import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useSelector } from "react-redux";
import Post from "./Post";

function Home() {
  const [profiles, setProfiles] = useState([]);
  const [user, setUser] = useState({ username: null, logoImage: null });
  const [posts, setPosts] = useState([]);
  const Navigator = useNavigate();
  const { isAuthenticated, userId, username } = useSelector(
    (state) => state.auth
  );
  console.log(
    "isAuthenticated, userId, username::: ",
    isAuthenticated,
    userId,
    username
  );

  useEffect(() => {
    if (!isAuthenticated) {
      Navigator("/login");
    } else {
      axios
        .get(`http://localhost:3001/home/`)
        .then(async (response) => {
          setUser(response.data.user);
          setPosts(response.data.posts);
          setProfiles(response.data.profiles);
        })
        .catch((err) => {
          if (err.response.status) {
            Navigator("/login");
          }
        });
    }
  }, []);

  return (
    <div>
      <NavBar />
      <div id="homePageDiv">
        <div id="homeRightSide">
          <div id="randomUsersContainer">
            {profiles.map((profile) => {
              return (
                <div
                  id="randomUserContainer"
                  onClick={() => Navigator(`/profile/${profile.username}`)}
                >
                  <img
                    id="randomUserImage"
                    src={profile.logoImage ? profile.logoImage : defaultLogo}
                  />
                  <label id="randomUserName">{profile.username}</label>
                </div>
              );
            })}
          </div>
        </div>
        <div id="homeMiddleSide">
          <div className="ProfilePageContainer" id="PostsContainer">
            {posts.map((post) => {
              return <Post postData={post} />;
            })}
          </div>
        </div>
        <div id="homeLeftSide">
          <div
            id="homeUserContainer"
            onClick={() => Navigator(`/profile/${username}`)}
          >
            <img
              id="homeUserImage"
              src={user.logoImage ? user.logoImage : defaultLogo}
            />
            <label id="randomUserName">{user.userName}</label>
            <label id="homeUserDescription">{user.description}</label>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
