import React, { useRef, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import NavBar from "./NavBar";
import { useSelector } from "react-redux";
import uploadImg from "../Resources/Images/upload.png";

function EditProfile() {
  const Navigator = useNavigate();
  const { isAuthenticated, userId, username, description } = useSelector(
    (state) => state.auth
  );
  const usernameRef = useRef("");
  const descriptionRef = useRef("");
  const imageRef = useRef(null);

  useEffect(() => {
    if (!isAuthenticated) {
      Navigator("/login");
    }
  }, [isAuthenticated, Navigator]);

  const handleProfileUpdate = () => {
    const data = {
      image: imageRef.current.files[0] || null,
      username: usernameRef.current.value || null,
      description: descriptionRef.current.value || null,
    };
    console.log("data::: ", data);

    axios
      .post(`http://localhost:3001/profile/edit/${userId}`, data, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((response) => console.log(response))
      .catch((err) => {
        if (err.response.status) {
          Navigator("/login");
        }
      });
  };

  return (
    <div>
      <NavBar />
      <div id="EditProfileContainer">
        <div id="EditProfileTitle">
          <label htmlFor="usernameInput">Username:</label>
          <input
            id="usernameInput"
            type="text"
            placeholder={username}
            ref={usernameRef}
          />
        </div>
        <div id="EditProfileDescription">
          <label htmlFor="descriptionInput">Description:</label>
          <textarea
            id="descriptionInput"
            placeholder={description}
            rows="4"
            cols="50"
            ref={descriptionRef}
          ></textarea>
        </div>
        <div id="EditProfileFile">
          <label htmlFor="fileInput">Picture:</label>
          <input id="fileInput" type="file" ref={imageRef} />
        </div>
        <button onClick={handleProfileUpdate}>Update Profile</button>
      </div>
    </div>
  );
}

export default EditProfile;
