import React, { useRef } from "react";
import NavBar from "./NavBar";
import submitIcon from "../Resources/Images/message.png";
import defaultLogo from "../Resources/Images/defaultLogo.png";
import Message from "../Components/Message";
import axios from "axios";

function Chat(props) {
  const MessageRef = useRef("");
  const imageRef = useRef(null);

  const handleEnter = (event) => {
    if (event.key === "Enter") {
      SendMessage();
    }
  };

  const SendMessage = () => {
    if (MessageRef.current.value != "" && MessageRef.current.value != " ") {
      const data = {
        senderId: props.senderId,
        receiverId: props.receiverId,
        text: MessageRef.current.value,
        image: imageRef.current.files[0],
      };
      console.log("data::: ", data);

      axios
        .post(`http://localhost:3001/chat/addmessage/1`, data)
        .then((response) => {
          console.log(response);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };

  return (
    <div>
      <NavBar />
      <div id="ChatPageDiv">
        <div id="ChatDiv">
          <div id="DmName">
            <label id="FriendName">Friend Name</label>
          </div>
          <div id="messageContainer">
            <Message />
          </div>
          <div id="AddCommentContainer">
            <input id="MessageImageInput" type="file" ref={imageRef} />
            <textarea
              id="addMessageInput"
              type="text"
              placeholder="Enter Comment"
              onKeyDown={handleEnter}
              ref={MessageRef}
            />
            <img
              id="SendMessage"
              src={submitIcon}
              alt="Submit"
              onClick={SendMessage}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
