import React, { useRef } from "react";
import NavBar from "./NavBar";
import submitIcon from "../Resources/Images/message.png";
import defaultLogo from "../Resources/Images/defaultLogo.png";
import Message from "../Components/Message";
function Chat() {
  const MessageRef = useRef("");
  const handleEnter = (event) => {
    if (event.key === "Enter") {
      SendMessage();
    }
  };
  const SendMessage = () => {
    if (MessageRef.current.value != "" && MessageRef.current.value != " ") {
      Navigator(`/search/${MessageRef.current.value}`);
    }
  };

  return (
    <div>
      <NavBar />
      <div id="ChatPageDiv">
        <div id="ChatDiv">
          <div id="DmName">
            <label id="FriendName">Friend Name</label>
          </div>
          <div id="messageContainer">
            <Message />
          </div>
          <div id="WriteCommentContainer">
            <textarea
              id="addMessageInput"
              type="text"
              placeholder="Enter Comment"
              onKeyDown={handleEnter}
              ref={MessageRef}
            />
            <img
              id="SendMessage"
              src={submitIcon}
              alt="Submit"
              onClick={SendMessage}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
