import React, { useState, useEffect } from "react";
import axios from "axios";
import Buffer from "buffer";
import defaultLogo from "../Resources/Images/defaultLogo.png";
import { imagefrombuffer } from "imagefrombuffer";
function Post() {
  const [viewImage, setViewImage] = useState(null);
  useEffect(() => {
    const data = { postid: "1" };
    axios
      .get("http://localhost:3001/post/getbypostid", {
        headers: { postid: "1" },
      })
      .then((response) => {
        console.log("response", response.data._streams[1].data);
        const buffer = response.data._streams[1].data;
        const url = imagefrombuffer({
          type: buffer.profilephoto?.contentType,
          data: buffer.profilephoto?.data?.data,
        });
        setViewImage(url);
      });
  }, []);
  return (
    <div>
      <div id="PostContainer">
        <div id="PostimageFrame">
          <img id="1" src={viewImage} />
        </div>
        <div id="PostRightSide">
          <div id="PostRightSideProfileTop">
            <img src={defaultLogo} id="PostImageLogo" alt="Profile" />
            <label id="PostProfileName">Yair Gabay</label>
          </div>
          <div id="PostCommentsContainer"></div>
        </div>
      </div>
    </div>
  );
}

export default Post;
