import React, { useRef } from "react";
import NavBar from "./NavBar";
import submitIcon from "../Resources/Images/message.png";
import defaultLogo from "../Resources/Images/defaultLogo.png";
import Message from "../Components/Message";
function Chat() {
  const handleEnter = (event) => {
    if (event.key === "Enter") {
      Search();
    }
  };
  const Search = () => {
    if (searchRef.current.value != "" && searchRef.current.value != " ") {
      Navigator(`/search/${searchRef.current.value}`);
    }
  };

  return (
    <div>
      <NavBar />
      <div id="ChatPageDiv">
        <div id="ChatDiv">
          <div id="DmName">
            <label id="FriendName">Friend Name</label>
          </div>
          <div id="messageContainer">
            <Message />
          </div>
          <div id="WriteCommentContainer">
            <textarea
              id="addMessageInput"
              type="text"
              placeholder="Enter Comment"
            />
            <img id="SendMessage" src={submitIcon} alt="Submit" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
