import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { useState } from "react";

function CreatePost() {
  const [image, setImage] = useState(null);
  const handleImageSelect = (e) => {
    setImage(e.target.files[0]);
  };
  const initialValues = {
    title: "",
    description: "",
    file: image,
  };

  const validationSchema = Yup.object({
    title: Yup.string().min(1).max(20).required("Title is required"),
    description: Yup.string().max(200).required("Description is required"),
    file: Yup.mixed().required("File is required"),
  });

  const handleSubmit = (values) => {
    const formData = new FormData();
    formData.append("image", values.file);

    axios.post("http://localhost:3001/post/create", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
      responseType: "arraybuffer",
    });
  };

  return (
    <div>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ setFieldValue }) => (
          <Form>
            <div id="CreatePostContainer">
              <div id="CreatePostContainerTitle">
                <label htmlFor="title">Title:</label>
                <Field
                  type="text"
                  id="title"
                  name="title"
                  placeholder="Enter title"
                />
                <ErrorMessage name="title" component="div" className="error" />
              </div>
              <div id="CreatePostContainerDescription">
                <label htmlFor="description">Description:</label>
                <Field
                  as="textarea"
                  id="description"
                  name="description"
                  placeholder="Enter description"
                  rows="4"
                  cols="50"
                />
                <ErrorMessage
                  name="description"
                  component="div"
                  className="error"
                />
              </div>
              <div id="CreatePostContainerFile">
                <label htmlFor="file">Upload File:</label>
                <input
                  id="file"
                  type="file"
                  name="file"
                  onChange={handleImageSelect}
                />
              </div>
              <button type="submit" id="CreatePostSubmitButton">
                Submit
              </button>
            </div>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default CreatePost;
