import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import defaultLogo from "../Resources/Images/defaultLogo.png";
import SearchBar from "../Components/SearchBar";
import axios from "axios";
import { login, logout } from "../authSlice";
import Cookies from "js-cookie";

function NavBar() {
  const Navigator = useNavigate();
  const dispatch = useDispatch();
  const { isAuthenticated, userId, username, logoImage } = useSelector(
    (state) => state.auth
  );

  useEffect(() => {
    const myCookie = Cookies.get("access-token");
    console.log(myCookie);

    if (!isAuthenticated) {
      axios
        .get("http://localhost:3001/auth/validate")
        .then((response) => {
          var imageUrl = null;
          if (response.status === 200) {
            if (response.data.logoImage) {
              const buffer = response.data.logoImage.data;
              var arrayBufferView = new Uint8Array(buffer);
              var blob = new Blob([arrayBufferView], { type: "image/png" });
              var urlCreator = window.URL || window.webkitURL;
              imageUrl = urlCreator.createObjectURL(blob);
            }
            dispatch(
              login({
                userId: response.data.userId,
                username: response.data.username,
                logoImage: imageUrl,
                description: response.data.description,
              })
            );
            Navigator("/");
          }
        })
        .catch((err) => {
          if (err.response && err.response.status === 401) {
            Navigator("/login");
          }
        });
    }
  }, [isAuthenticated, Navigator, dispatch]);

  return (
    <nav id="NavBar">
      <div id="NavBarimageFrame">
        <img
          src={logoImage ? logoImage : defaultLogo}
          id="NavBarImageLogo"
          alt="Profile"
          onClick={() => Navigator(`/profile/${username}`)}
        />
      </div>
      <ul id="main-buttons">
        <li>
          <button
            onClick={() => Navigator(`/profile/${username}`)}
            className="AB"
          >
            My Profile
          </button>
        </li>
        <li>
          <button onClick={() => Navigator("/about")}>About</button>
        </li>
        <li>
          <button onClick={() => Navigator("/services")} className="AB">
            Services
          </button>
        </li>
        <li>
          <button onClick={() => Navigator("/contact")} className="AB">
            Contact
          </button>
        </li>
      </ul>
      <div id="NavBarSearchDiv">
        <SearchBar />
      </div>
    </nav>
  );
}

export default NavBar;
