import { useFormik } from "formik";
import axios from "axios";
import Cookies from "js-cookie";
import React, { useState, useEffect, useContext } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../authSlice";
import { useNavigate } from "react-router-dom";

function Login() {
  const Navigator = useNavigate();
  const dispatch = useDispatch();
  const { isAuthenticated, userId, username } = useSelector(
    (state) => state.auth
  );
  const formik = useFormik({
    initialValues: {
      username: "",
      password: "",
    },
    onSubmit: (values) => {
      const data = {
        username: values.username,
        password: values.password,
      };
      console.log("request send login");
      axios.post("http://localhost:3001/auth/login", data).then((response) => {
        dispatch(
          login({
            userId: data.userId,
            username: data.username,
            logoImage: data.logoImage,
          })
        );

        Navigator(`/profile/${data.username}`);
      });
    },
  });

  return (
    <div className="loginContainer">
      <div className="title">
        <label id="Logintitle">Login</label>
      </div>
      <form onSubmit={formik.handleSubmit}>
        <label htmlFor="username">Username</label>
        <input
          id="username"
          type="text"
          placeholder="Enter your username"
          {...formik.getFieldProps("username")}
        />
        <label htmlFor="password">Password</label>
        <input
          id="password"
          type="password"
          placeholder="Enter your password"
          {...formik.getFieldProps("password")}
        />
        <button type="submit" className="AB">
          Submit
        </button>
      </form>
      <a href="/register" className="AB">
        Don't have an account? Register
      </a>
    </div>
  );
}

export default Login;
