import React, { useRef, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import NavBar from "./NavBar";
import { useSelector } from "react-redux";

axios.defaults.withCredentials = true;

function EditProfile() {
  const Navigator = useNavigate();
  const { isAuthenticated, userId, username } = useSelector(
    (state) => state.auth
  );
  useEffect(() => {
    if (!isAuthenticated) {
      Navigator("/login");
    }
  });

  const usernameRef = useRef("");
  const descriptionRef = useRef("");
  const imageRef = useRef(null);

  axios.defaults.withCredentials = true;

  const SubmitPost = () => {
    const data = {
      image: imageRef.current.files[0],
      username: usernameRef.current.value,
      description: descriptionRef.current.value,
    };
    console.log("data::: ", data);

    axios
      .post(`http://localhost:3001/profile/edit/${userId}`, data, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        if (err.response.status) {
          Navigator("/login");
        }
      });
  };

  return (
    <div>
      <NavBar />
      <div id="CreatePostContainer">
        <div id="CreatePostContainerTitle">
          <label htmlFor="usernameInput">username:</label>
          <input
            id="usernameInput"
            type="text"
            placeholder={username}
            ref={usernameRef}
          />
        </div>
        <div id="CreatePostContainerDescription">
          <label htmlFor="descriptionInput">Description:</label>
          <textarea
            id="descriptionInput"
            placeholder="Enter description"
            rows="4"
            cols="50"
            ref={descriptionRef}
          ></textarea>
        </div>
        <div id="CreatePostContainerFile">
          <label htmlFor="fileInput">Picture:</label>
          <input id="fileInput" type="file" ref={imageRef} />
        </div>
        <button id="CreatePostSubmitButton" onClick={SubmitPost}>
          Submit
        </button>
      </div>
    </div>
  );
}

export default EditProfile;
