import NavBar from "./NavBar";
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useSelector } from "react-redux";
import Post from "./Post";

function Home() {
  const [profilesData, setProfiles] = useState([]);
  const [user, setuser] = useState(null);
  const [posts, setposts] = useState([]);
  const Navigator = useNavigate();
  const { isAuthenticated, userId, username } = useSelector(
    (state) => state.auth
  );
  useEffect(() => {
    if (!isAuthenticated) {
      Navigator("/login");
    } else {
      axios
        .get(`http://localhost:3001/home/getdata`)
        .then(async (response) => {
          setposts[response.data.posts];
        })
        .catch((err) => {
          if (err.response.status) {
            Navigator("/login");
          }
        });
    }
  }, []);

  return (
    <div>
      <NavBar />
      <div id="homePageDiv">
        <div id="homeRightSide">
          <div id="randomUsersContainer"></div>
        </div>
        <div id="homeMiddleSide">
          <div className="ProfilePageContainer" id="PostsContainer">
            {posts.map((post) => {
              return <Post postData={post} />;
            })}
          </div>
        </div>
        <div id="homeLeftSide"></div>
      </div>
    </div>
  );
}

export default Home;
